export declare function isNumber(v: any): v is number;
export declare function isNull(v: any): v is null;
export declare function isUndefined(v: any): v is undefined;
export declare function isBoolean(v: any): v is boolean;
export declare function isObject<T = Record<string, any>>(v: any): v is T;
export declare function isArray<T = any[]>(v: any): v is T;
export declare function isNilVal(v: any, ...args: any[]): v is undefined | null;
export declare function isNumeric(value: any): boolean;
export declare function isFunc<T = AnyFunction>(v: any): v is T;
